mapper.insert:
insert  into VTGLOSSARY (
	WORD_IDX
	, WORD
	, WORD_EN
	, WORD_ABBR
	, WORD_DESC 
	, REG_ID
	, REG_DT
	, UPD_ID
	, UPD_DT
) 
values (
	#{wordIdx}
	, #{word}
	, #{wordEn}
	, #{wordAbbr}
	, #{wordDesc} 
	, #{userId}
	, #{userId}
)

mapper.update:
update VTGLOSSARY 
set 
	USER_ROLE =#{role}
	,ACCEPT_YN=#{acceptyn}
where WORD_IDX =#{wordIdx}
