mapper.insert:
insert  into VTGLOSSARY (
	WORD_IDX
	, WORD
	, WORD_EN
	, WORD_ABBR
	, WORD_DESC 
	, REG_ID
	, UPD_ID
) 
values (
	(select (nvl(max(WORD_IDX),0)+1) as maxval from VTGLOSSARY)
	, #{word}
	, #{wordEn}
	, #{wordAbbr}
	, #{wordDesc} 
	, #{userId}
	, #{userId}
)

mapper.update:
update VTGLOSSARY 
set
	WORD = #{word}
	, WORD_EN = #{wordEn}
	, WORD_ABBR = #{wordAbbr}
	, WORD_DESC = #{wordDesc}
	, UPD_ID = #{userId}
	, UPD_DT =current_date
where WORD_IDX = #{wordIdx}
