mapper.select:
select b.* , a.VNAME , 
	(select count(1) from VTDATABASE_MANAGER subt 
	 where subt.VCONNID = a.VCONNID 
	 and subt.VIEWID = #{userId} 
	) MANAGER_CNT
from VTCONNECTION a 
	,(
		select distinct VCONNID from VTDATABASE_GROUP_DB where GROUP_ID in (
			select GROUP_ID  from VTDATABASE_GROUP_USER where VIEWID =#{viewid}
		)
	)b 
where a.VCONNID = b.VCONNID
and a.USE_YN = 'Y'

mapper.delete:
delete from VTDATABASE_USER_AUTHORITY
where 1=1


		and AUTH_TYPE = 'M'


		and AUTH_TYPE = 'U'


and VCONNID = #{vconnid}
and VIEWID  = #{viewid}

mapper.select:
select count(1)  cnt
from VTDATABASE_MANAGER 
where VCONNID = #{vconnid} 
and VIEWID = #{userId}

mapper.update:
update VTUSER 
set 
	USER_ROLE =#{role}
	,ACCEPT_YN=#{acceptyn}
where VIEWID =#{viewid}

update VTUSER 
set 
	BLOCK_YN=#{blockYn}
where VIEWID =#{userid}
and USER_ROLE in ('USER','GUEST')

update VTQNA
set
	ANSWER =#{answer}
	,ANSWER_ID =#{userid}
	,ANSWER_DT = CURRENT_TIMESTAMP
where QNAID=#{qnaid}
