mapper.update:
update VTUSER 
set 
	USER_ROLE =#{role}
	,ACCEPT_YN=#{acceptyn}
where VIEWID =#{viewid}

update VTQNA
set
	ANSWER =#{answer}
	,UPD_ID =#{cre_id}
	,UPD_DT = CURRENT TIMESTAMP
where QNAID=#{qnaid}

mapper.delete:
delete from VTDATABASE_ROLE
where ROLE_TYPE = 'U'
and VCONNID = #{vconnid}

	#{item}

mapper.insert:
insert into VTDATABASE_ROLE (VCONNID, VIEWID ,ROLE_TYPE, REG_ID ,REG_DT)
values(
	#{vconnid}
	, #{viewid}
	,'U'
	, #{uid}
	,CURRENT TIMESTAMP
)
