mapper.insert:
insert into VTSQL_LOG (
	VCONNID
	, VIEWID
	, START_TIME
	, S_MM
	, S_DD
	, S_HH
	, END_TIME
	, DELAY_TIME
	, LOG_SQL
	, USR_IP
	, RESULT_COUNT
	, COMMAND_TYPE
)
values( 
	#{vconnid}
	, #{viewid}
	, #{startTime}
	, #{sMm}
	, #{sDd}
	, #{sHh}
	, #{endTime}
	, #{delayTime}
	, #{logSql}
	, #{usrIp}
	, #{resultCount}
	, #{commandType}
)

insert  into VTSQL_SQLFILE (
	VIEWID
	, VCONNID
	, SQL_ID
	, GUERY_TITLE
	, QUERY_CONT
	, SQL_PARAM
	, VIEW_YN
	, TAB_YN
	, REG_DT
	, UPD_DT
) 
values(
	#{userid}
	, #{vconnid}
	, #{sqlId}
	, #{sqlTitle}
	, #{sql}
	, #{sqlParam}
	,'Y'
	,'Y'
	, CURRENT TIMESTAMP
	, CURRENT TIMESTAMP
)

mapper.update:
update VTSQL_SQLFILE 
set
	UPD_DT = CURRENT TIMESTAMP


		, GUERY_TITLE = #{sqlTitle}


		,TAB_YN  = 'Y'
		,VIEW_YN = 'Y'


		, QUERY_CONT = #{sql}
		, SQL_PARAM =#{sqlParam}


where sql_id = #{sqlId}
and VCONNID = #{vconnid}
and VIEWID = #{userid}

update VTSQL_SQLFILE 
set
	VIEW_YN = 'N'
where VCONNID = #{vconnid} 
and VIEWID = #{userid}
and VIEW_YN = 'Y'

update VTSQL_SQLFILE 
set
	TAB_YN ='N'
	,VIEW_YN ='N'
where VCONNID = #{vconnid} 
and VIEWID = #{userid}
and sql_id = #{sqlId}

mapper.select:
select count(1) as TOTALCNT 
from VTSQL_SQLFILE
where VCONNID = #{vconnid}
and VIEWID = #{userid}
and GUERY_TITLE like '%'||#{custom.searchVal}||'%'

select * from VTSQL_SQLFILE
where VCONNID = #{vconnid}
and VIEWID = #{userid}
and GUERY_TITLE like '%'||#{custom.searchVal}||'%' 
order by GUERY_TITLE asc

mapper.insert:
insert  into VTSQL_HISTORY (
	VCONNID
	, VIEWID
	, HISTORYID
	, START_TIME
	, END_TIME
	, DELAY_TIME
	, LOG_SQL
	, USR_IP
	, ERROR_LOG 
) 
values(
	#{vconnid}
	, #{viewid}
	, #{historyId}
	, #{startTime}
	, #{endTime}
	, #{delayTime}
	, #{logSql}
	, #{usrIp}
	, #{errorLog} 
)

mapper.select:
select count(1) TOTAL_CNT
from VTSQL_HISTORY a
where 1=1
and VCONNID=#{customParam.conuid}
and VIEWID=#{customParam.uid}
and LOG_SQL like '%'||#{keyword}||'%'
