mapper.select:
select count(1)as CNT 
from VTUSER
where uid =#{uid}

mapper.insert:
insert  into VTUSER (
	VIEWID 
	, UID 
	, UPW 
	, UNAME 
	, ORG_NM 
	, DEPT_NM 
	, UEMAIL 
	, USER_ROLE 
	, DESCRIPTION 
	, ACCEPT_YN
	, REG_ID 
	, REG_DT
	, UPD_ID
	, UPD_DT
) 
values(
	#{viewid}
	, #{uid}
	, #{upw}
	, #{uname}
	, #{orgNm}
	, #{deptNm}
	, #{uemail}
	, 'GUEST'
	, #{description}
	, #{acceptYn}
	, #{creId}
	, CURRENT_TIMESTAMP
	, #{creId}
	, CURRENT_TIMESTAMP
)

mapper.update:
update VTUSER 
set 
	 UNAME =#{uname}
	, ORG_NM =#{orgNm}
	, DEPT_NM =#{deptNm}
	, UEMAIL =#{uemail}
	, DESCRIPTION =#{description}
	, LANG =#{lang}
	, UPD_DT = CURRENT_TIMESTAMP
where VIEWID =#{viewid}

mapper.select:
select UPW
from VTUSER a
where VIEWID =#{viewid}

mapper.update:
update VTUSER 
set 
	 UPW = #{upw}
where VIEWID =#{viewid}

mapper.insert:
insert  into VTQNA (
	QNAID 
	, TITLE 
	, QUESTION 
	, REG_ID 
	, REG_DT 
)
values(
	#{qnaid}
	, #{title}
	, #{question}
	, #{userid}
	, CURRENT_TIMESTAMP
)

mapper.update:
update VTQNA
set
	TITLE =#{title}
	,QUESTION =#{question}
	,UPD_DT = CURRENT_TIMESTAMP
where QNAID=#{qnaid}
and REG_ID = #{userid}
and ANSWER_ID IS NULL

update VTQNA 
set
	DEL_YN ='Y'
where QNAID=#{qnaid}
and REG_ID = #{userid}
and ANSWER_ID IS NULL

mapper.select:
select c.* , d.UNAME as SEND_NM , d.UID  as SEND_ID
from (
	select a.MEMO_TITLE , a.MEMO_ID, a.MEMO_CONT, SEND_ID, to_char(REG_DT,'YYYY-MM-DD HH24:MI:SS') as REG_DT , VIEW_DT
	from VTMEMO a , VTMEMO_USER b 
	where a.MEMO_ID = b.MEMO_ID 
	and RECV_ID = #{uid}
	and VIEW_DT is null
	order by reg_dt desc
) c left outer join VTUSER d 
where c.SEND_ID  = d.VIEWID

select count(1) CNT from VTMEMO a
where REG_ID = #{customParam.uid}
, VTMEMO_USER b where a.MEMO_ID = b.MEMO_ID and RECV_ID = #{customParam.uid}
and MEMO_TITLE like '%'||#{keyword}||'%'

mapper.delete:
delete from VTMEMO_USER 
where 1=1


		and SEND_ID = (select REG_ID from VTMEMO WHERE MEMO_ID =#{memoId})
		and VIEW_DT is null


		and RECV_ID = #{uid}


and MEMO_ID =#{memoId}

mapper.insert:
insert  into VTMEMO (
	MEMO_ID
	, PARENT_MEMO_ID
