	, REG_DT
) 
values(
	#{memoId}
	, #{parentMemoId}
	, #{memoTitle}
	, #{memoCont}
	, #{regId}
	, CURRENT_TIMESTAMP
)

mapper.update:
update VTMEMO_USER 
set 
	VIEW_DT = CURRENT_TIMESTAMP
where RECV_ID = #{uid}
and MEMO_ID = #{memo_id}

mapper.delete:
delete from VTDATABASE_USER_AUTHORITY
where 1=1


		and AUTH_TYPE = 'M'


		and AUTH_TYPE = 'U'


and VCONNID = #{vconnid}
and VIEWID  = #{viewid}

mapper.select:
select count(1)  cnt
from VTDATABASE_USER_AUTHORITY 
