mapper.update:
update VTCONNECTION 
set 
	VNAME =#{vname}
	, VURL =#{vurl}
	, VDBSCHEMA =#{vdbschema}
	, VDRIVER =#{vdriver} 
	, VTYPE =#{vtype}
	, VID =#{vid}
	, VPW =#{vpw}
	, VCONNOPT =#{vconnopt}
	, VPOOLOPT =#{vpoolopt}
	, VSQL=#{vsql}
where VCONNID =#{vconid}

insert into VTDATABASE_MANAGER_ROLE (VCONNID, VIEWID , UPD_DT)
values(
	#{vconid}
	, #{viewid}
	,CURRENT TIMESTAMP
)

mapper.insert:
insert  into VTMENU (
	DB_TYPE 
	, MENU_ID 
	, P_MENU_ID 
	, UNIQUE_ID 
	, MENU_NM 
	, MENU_URL 
	, MENU_PARAM 
	, POPUP_YN 
	, VIEW_OPTION 
	, SORT_ORDER 
	, DEPTH
	, USE_YN
)values(
	#{db_type}
	,#{menu_id}
	,#{p_menu_id}
	,#{unique_id}
	,#{menu_nm}
	,#{menu_url}
	,#{menu_param}
	,#{popup_yn}
	,#{view_option}
	,#{sort_order}
	,#{depth}
	,#{use_yn}
)

mapper.update:
update VTMENU 
set 
	DB_TYPE=#{db_type}
	,UNIQUE_ID=#{unique_id}
	,MENU_NM=#{menu_nm}
	,MENU_URL=#{menu_url}
	,MENU_PARAM=#{menu_param}
	,POPUP_YN=#{popup_yn}
	,VIEW_OPTION=#{view_option}
	,SORT_ORDER=#{sort_order}
	,DEPTH=#{depth}
	,USE_YN=#{use_yn}
where MENU_ID =#{menu_id}

mapper.select:
SELECT DBTYPE,DBDRIVER,DRIVER_DESC 
FROM VTDBTYPE_DRIVER
WHERE DBTYPE = #{db_type}
