mapper.insert:
insert into VTCONNECTION (
	VCONNID
	,VNAME 
	,VURL 
	,VDBSCHEMA 
	,VDRIVER 
	,VTYPE 
	,VID 
	,VPW
	,VCONNOPT 
	,VPOOLOPT
	,VQUERY
	,REG_ID
	,REG_DT
	,UPD_ID
	,UPD_DT
)values (
	#{vconid}
	,#{vname}
	,#{vurl} 
	,#{vdbschema} 
	,#{vdriver} 
	,#{vtype}
	,#{vid}
	,#{vpw}
	,#{vconnopt}
	,#{vpoolopt}
	,#{vquery}
	,#{uid}
	,CURRENT TIMESTAMP
	,#{uid}
	,CURRENT TIMESTAMP
)

mapper.update:
update VTCONNECTION 
set 
	VNAME =#{vname}
	, VURL =#{vurl}
	, VDBSCHEMA =#{vdbschema}
	, VDRIVER =#{vdriver} 
	, VTYPE =#{vtype}
	, VID =#{vid}
	, VPW =#{vpw}
	, VCONNOPT =#{vconnopt}
	, VPOOLOPT =#{vpoolopt}
	, VQUERY=#{vquery}
	, UPD_ID = #{uid}
	, UPD_DT =CURRENT TIMESTAMP 
where VCONNID =#{vconid}

mapper.delete:
delete from VTDATABASE_ROLE
where and b.ROLE_TYPE = 'M' 
and VCONNID = #{vconid}

mapper.update:
insert into VTDATABASE_ROLE (VCONNID, VIEWID , ROLE_TYPE, REG_ID, REG_DT)
values(
	#{vconid}
	, #{viewid}
	, 'M'
	, #{uid}
	,CURRENT TIMESTAMP
)

mapper.insert:
insert  into VTMENU (
	DB_TYPE 
	, MENU_ID 
	, P_MENU_ID 
	, UNIQUE_ID 
	, MENU_NM 
	, MENU_URL 
	, MENU_PARAM 
	, POPUP_YN 
	, VIEW_OPTION 
	, SORT_ORDER 
	, DEPTH
	, USE_YN
)values(
	#{db_type}
	,#{menu_id}
	,#{p_menu_id}
	,#{unique_id}
	,#{menu_nm}
	,#{menu_url}
	,#{menu_param}
	,#{popup_yn}
	,#{view_option}
	,#{sort_order}
	,#{depth}
	,#{use_yn}
)

mapper.update:
update VTMENU 
set 
	DB_TYPE=#{db_type}
	,UNIQUE_ID=#{unique_id}
	,MENU_NM=#{menu_nm}
	,MENU_URL=#{menu_url}
	,MENU_PARAM=#{menu_param}
	,POPUP_YN=#{popup_yn}
	,VIEW_OPTION=#{view_option}
	,SORT_ORDER=#{sort_order}
	,DEPTH=#{depth}
	,USE_YN=#{use_yn}
where MENU_ID =#{menu_id}

mapper.select:
SELECT DRIVER_ID,DBTYPE,DBDRIVER,DRIVER_DESC
FROM VTDBTYPE_DRIVER
WHERE DBTYPE = #{db_type}
