mapper.update:
update vtconnection 
set 
	vname =#{vname}
	, vurl =#{vurl}
	, vdriver =#{vdriver} 
	, vtype =#{vtype}
	, vid =#{vid}
	, vpw =#{vpw}
	, vconnopt =#{vconnopt}
	, vpoolopt =#{vpoolopt}
	, vsql=#{vsql}
where vconnid =#{vconid}

insert into vtdatabase_manager_role (vconnid, viewid , upd_dt)
values(
	#{vconid}
	, #{viewid}
	,CURRENT TIMESTAMP
)

mapper.insert:
insert  into vtmenu (
	db_type 
	, menu_id 
	, p_menu_id 
	, unique_id 
	, menu_nm 
	, menu_url 
	, menu_param 
	, popup_yn 
	, view_option 
	, sort_order 
	, depth
	, use_yn
)values(
	#{db_type}
	,#{menu_id}
	,#{p_menu_id}
	,#{unique_id}
	,#{menu_nm}
	,#{menu_url}
	,#{menu_param}
	,#{popup_yn}
	,#{view_option}
	,#{sort_order}
	,#{depth}
	,#{use_yn}
)

mapper.update:
update vtmenu 
set 
	db_type=#{db_type}
	,unique_id=#{unique_id}
	,menu_nm=#{menu_nm}
	,menu_url=#{menu_url}
	,menu_param=#{menu_param}
	,popup_yn=#{popup_yn}
	,view_option=#{view_option}
	,sort_order=#{sort_order}
	,depth=#{depth}
	,use_yn=#{use_yn}
where menu_id =#{menu_id}
