mapper.update:
update VTUSER 
set 
	ROLE =#{role}
	,ACCEPT_YN=#{acceptyn}
where VIEWID =#{viewid}

update VTQNA
set
	ANSWER =#{answer}
	,UPD_ID =#{cre_id}
	,UPD_DT = CURRENT TIMESTAMP
where QNAID=#{qnaid}

mapper.insert:
insert into VTDATABASE_USER_ROLE (VCONNID, VIEWID , UPD_ID ,UPD_DT)
values(
	#{vconnid}
	, #{viewid}
	, #{upd_id}
	,CURRENT TIMESTAMP
)

mapper.select:
select 
	COMMAND_TYPE X_COL, count(1) as Y_COL
from VTSQL_LOG a
where	VCONNID=#{vconnid}
and START_TIME between #{s_date} and	#{e_date}
group by COMMAND_TYPE

select b.UNAME label , CNT as DATA from 
(
	select 
		VIEWID, count(1) cnt 
	from VTSQL_LOG a
	where	VCONNID=#{vconnid}
	and START_TIME between #{s_date} and	#{e_date}

		AND COMMAND_TYPE =#{command_type}

	group by VIEWID 
	order by CNT desc	  
	FETCH FIRST 5 ROWS ONLY
) a , VTUSER b
where a.VIEWID = b.VIEWID
