mapper.insert:
insert into VTSQL_LOG (
	VCONNID
	, VIEWID
	, START_TIME
	, S_MM
	, S_DD
	, S_HH
	, END_TIME
	, DELAY_TIME
	, LOG_SQL
	, RESULT_COUNT
	, COMMAND_TYPE
)
values( 
	#{vconnid}
	, #{viewid}
	, #{start_time}
	, #{s_mm}
	, #{s_dd}
	, #{s_hh}
	, #{end_time}
	, #{delay_time}
	, #{log_sql}
	, #{result_count}
	, #{command_type}
)

insert  into VTSQL_BOARD (
	VIEWID
	, VCONNID
	, SQL_ID
	, GUERY_TITLE
	, QUERY_CONT
	, SQL_PARAM
	, REG_DT
	, UPD_DT
) 
values(
	#{userid}
	, #{vconnid}
	, #{sqlId}
	, #{sqlTitle}
	, #{sql}
	, #{sqlParam}
	, CURRENT TIMESTAMP
	, CURRENT TIMESTAMP
)

mapper.update:
update VTSQL_BOARD 
set
	 GUERY_TITLE = #{sqlTitle}
	, QUERY_CONT = #{sql}
	, UPD_DT = CURRENT TIMESTAMP
	, SQL_PARAM =#{sqlParam}
where sql_id = #{sqlId}
and VCONNID = #{vconnid}
and VIEWID = #{userid}

mapper.select:
select * from VTSQL_BOARD
where VCONNID = #{vconnid}
and VIEWID = #{userid}
order by upd_dt desc
FETCH FIRST 1 ROWS ONLY

select count(1) as TOTALCNT 
from VTSQL_BOARD
where VCONNID = #{vconnid}
and VIEWID = #{userid}
and GUERY_TITLE like '%'||#{customInfo.searchVal}||'%'


select * from VTSQL_BOARD
where VCONNID = #{vconnid}
and VIEWID = #{userid}
and GUERY_TITLE like '%'||#{customInfo.searchVal}||'%' 
order by upd_dt desc OFFSET ${pageStartNum} ROWS FETCH FIRST ${customInfo.rows} ROWS ONLY
