mapper.insert:
insert into vtsql_log (
	vconnid
	, uid
	, start_time
	, s_mm
	, s_dd
	, s_hh
	, end_time
	, delay_time
	, log_sql
	, result_count
	, command_type
)
values( 
	#{vconnid}
	, #{uid}
	, #{start_time}
	, #{s_mm}
	, #{s_dd}
	, #{s_hh}
	, #{end_time}
	, #{delay_time}
	, #{log_sql}
	, #{result_count}
	, #{command_type}
)

insert  into VTSQL_BOARD (
	UID
	, VCONNID
	, SQL_ID
	, GUERY_TITLE
	, QUERY_CONT
	, REG_DT
	, UPD_DT
) 
values(
	#{uid}
	, #{vconnid}
	, #{sql_id}
	, #{sqlTitle}
	, #{sql}
	, CURRENT TIMESTAMP
	, CURRENT TIMESTAMP
)

mapper.update:
update VTSQL_BOARD 
set
	 GUERY_TITLE = #{sqlTitle}
	, QUERY_CONT = #{sql}
	, UPD_DT = CURRENT TIMESTAMP
where sql_id = #{sql_id}
and VCONNID = #{vconnid}
and UID = #{uid}

mapper.select:
select * from VTSQL_BOARD
where VCONNID = #{vconnid}
and UID = #{uid}
order by upd_dt desc
FETCH FIRST 1 ROWS ONLY

select count(1) as TOTALCNT 
from VTSQL_BOARD
where VCONNID = #{vconnid}
and UID = #{uid}

select * from VTSQL_BOARD
where VCONNID = #{vconnid}
and UID = #{uid}
order by upd_dt desc
