mapper.select:
select count(1)as CNT 
from VTUSER
where uid =#{uid}

mapper.insert:
insert  into VTUSER (
	VIEWID 
	, UID 
	, UPW 
	, UNAME 
	, ORG_NM 
	, DEPT_NM 
	, UEMAIL 
	, ROLE 
	, DESCRIPTION 
	, ACCEPT_YN
	, CRE_ID 
	, UPD_ID
	, CRE_DT
) 
values(
	#{viewid}
	, #{uid}
	, #{upw}
	, #{uname}
	, #{orgNm}
	, #{deptNm}
	, #{uemail}
	, 'GUEST'
	, #{description}
	, #{acceptYn}
	, #{creId}
	, #{creId}
	, CURRENT TIMESTAMP
)

mapper.update:
update VTUSER 
set 
	UPW=#{upw}
	, UNAME =#{uname}
	, ORG_NM =#{org_nm}
	, DEPT_NM =#{dept_nm}
	, UEMAIL =#{uemail}
	, DESCRIPTION =#{description}
	, UPD_DT = CURRENT TIMESTAMP
where VIEWID =#{viewid}

mapper.insert:
insert  into VTQNA (
	QNAID 
	, TITLE 
	, QUESTION 
	, CRE_ID 
	, CRE_DT 
	, DEL_YN
	, ANSWER
)
values(
	#{qnaid}
	, #{title}
	, #{question}
	, #{cre_id}
	, CURRENT TIMESTAMP
	, 'N'
	, ''
)

mapper.update:
update VTQNA
set
	TITLE =#{title}
	,QUESTION =#{question}
where QNAID=#{qnaid}

mapper.insert:
insert  into VTMEMO (
