mapper.select:
select count(1)as CNT 
from VTUSER
where uid =#{uid}

mapper.insert:
insert  into VTUSER (
	VIEWID 
	, UID 
	, UPW 
	, UNAME 
	, ORG_NM 
	, DEPT_NM 
	, UEMAIL 
	, ROLE 
	, DESCRIPTION 
	, ACCEPT_YN
	, CRE_ID 
	, UPD_ID
	, CRE_DT
) 
values(
	#{viewid}
	, #{uid}
	, #{upw}
	, #{uname}
	, #{orgNm}
	, #{deptNm}
	, #{uemail}
	, 'GUEST'
	, #{description}
	, #{acceptYn}
	, #{creId}
	, #{creId}
	, CURRENT TIMESTAMP
)

mapper.update:
update VTUSER 
set 
	 UNAME =#{uname}
	, ORG_NM =#{orgNm}
	, DEPT_NM =#{deptNm}
	, UEMAIL =#{uemail}
	, DESCRIPTION =#{description}
	, UPD_DT = CURRENT TIMESTAMP
where VIEWID =#{viewid}

mapper.select:
select UPW
from VTUSER a
where VIEWID =#{viewid}

mapper.update:
update VTUSER 
set 
	 UPW = #{upw}
where VIEWID =#{viewid}

mapper.insert:
