mapper.select:
select c.* , d.UNAME as SEND_NM , d.UID  as SEND_ID
from (
	select a.MEMO_TITLE , a.MEMO_ID, a.MEMO_CONT, SEND_ID, to_char(REG_DT,'YYYY-MM-DD HH24:MI:SS') as REG_DT , VIEW_DT
	from VTMEMO a , VTMEMO_USER b 
	where a.MEMO_ID = b.MEMO_ID 
	and RECV_ID = #{uid}
	and VIEW_DT is null
	order by reg_dt desc
) c left outer join VTUSER d 
where c.SEND_ID  = d.VIEWID

select count(1) CNT
from VTMEMO a , VTMEMO_USER b 
where a.MEMO_ID = b.MEMO_ID 


		and SEND_ID = #{customParam.uid}


		and RECV_ID = #{customParam.uid}


and MEMO_TITLE like '%'||#{keyword}||'%'

select c.* , d.UNAME as SEND_NM , d.UID  as SEND_ID
from (
	select a.MEMO_TITLE , a.MEMO_ID, a.MEMO_CONT, SEND_ID, to_char(REG_DT,'YYYY-MM-DD HH24:MI:SS') as REG_DT  , VIEW_DT
	from VTMEMO a , VTMEMO_USER b 
	where a.MEMO_ID = b.MEMO_ID 


			and SEND_ID = #{customParam.uid}


			and RECV_ID = #{customParam.uid}


	and MEMO_TITLE like '%'||#{keyword}||'%' 
	order by reg_dt desc OFFSET ${first} ROWS FETCH FIRST ${last} ROWS ONLY
) c left outer join VTUSER d 
where c.SEND_ID  = d.VIEWID

mapper.delete:
delete from VTMEMO_USER 
where 1=1


		and SEND_ID = (select REG_ID from VTMEMO WHERE MEMO_ID =#{memoId})
		and VIEW_DT is null


		and RECV_ID = #{uid}


and MEMO_ID =#{memoId}

mapper.insert:
insert  into VTMEMO (
	MEMO_ID
	, MEMO_TITLE
	, MEMO_CONT
	, REG_ID
	, REG_DT
) 
values(
	#{memo_id}
	, #{memo_title}
	, #{memo_cont}
	, #{uid}
	, CURRENT TIMESTAMP
)

update VTMEMO_USER 
set 
	VIEW_DT = CURRENT TIMESTAMP
where RECV_ID = #{uid}
and MEMO_ID = #{memo_id}
