mapper.select:
select * 
from VTPREFERENCES 
where VCONNID =#{vconnid}
and VIEWID =#{userid}
and PREF_KEY =#{prefKey}

mapper.insert:
insert  into VTPREFERENCES (VCONNID,VIEWID,PREF_KEY,PREF_VAL,REG_DT,UPD_DT ) 
values(
	#{vconnid}
	, #{userid}
	, #{prefKey}
	, #{prefVal}
	, CURRENT TIMESTAMP
	, CURRENT TIMESTAMP
)

mapper.update:
update VTPREFERENCES 
set
	PREF_VAL = #{prefVal}
	, UPD_DT = CURRENT TIMESTAMP
where	VCONNID = #{vconnid} 
and	VIEWID = #{userid} 
and	PREF_KEY = #{prefKey}
